mapper.update:
UPDATE clothes
SET
schoolName=#{schoolName}, clothessize=#{size}, clothesType=#{type},clothesPrice=#{price},donorID=#{donor},
gender=#{gender}, status=#{status}
WHERE
clothesID=#{clothesID}

mapper.select:
SELECT * FROM clothes


        schoolName=#{schoolName}


        AND clothessize=#{size}


        AND clothesType=#{type}


        AND gender=#{gender}


        AND donorID=#{donor}


        AND status=#{status}
